import React from 'react';


import Shell from '@react/react-spectrum/Shell/js/Shell';
import ShellHeader from '@react/react-spectrum/Shell/js/ShellHeader';
import ShellHelp from '@react/react-spectrum/Shell/js/ShellHelp';
import ShellUserProfile from '@react/react-spectrum/Shell/js/ShellUserProfile';
import ShellActions from '@react/react-spectrum/Shell/js/ShellActions';
import ShellMenu from '@react/react-spectrum/Shell/js/ShellMenu';
import ShellSolution from '@react/react-spectrum/Shell/js/ShellSolution';
import ShellWorkspace from '@react/react-spectrum/Shell/js/ShellWorkspace';

import AdobeSocial from '@react/react-spectrum/Icon/AdobeSocial';

export default class ProtoShell extends React.Component {
    render(){
      return (
        <div>
        <Shell>

          <ShellHeader
              homeTitle={"Prototype"}
              homeIcon={<AdobeSocial/>}
>
            <ShellSolution></ShellSolution>
            <button onClick={this.ScrubPage}>clicky</button>
            <ShellActions>
            <ShellHelp></ShellHelp>
            <ShellUserProfile 
                avatarUrl={"https://www.w3schools.com/howto/img_avatar.png"} 
                name={"frank"}
                heading={"superboss"}
                subheading={"Person"}
                >
            </ShellUserProfile>


            </ShellActions>

          </ShellHeader>

        </Shell>
        </div>
      )
    }
  ScrubPage = () => {
    //Clears all event listeners
    let getErrthang = document.getElementById('proto1');
    let gotit = getErrthang.cloneNode(true);
    let theBody = document.getElementById('proto1');
    theBody.parentNode.replaceChild(gotit, theBody);
    //Clears all link functionality and applies specific functionality to links
    let getAllLinks = document.getElementsByTagName('a');
    Array.from(getAllLinks).forEach(
      (el) => {
        //this clears all a hrefs links
        el.onclick = () => { return false };
      }
    )
    //Add Custom Functionality here
    let getCheckboxes = document.querySelectorAll('.spectrum-Table-checkboxCell');
    Array.from(getCheckboxes).forEach(
      (el) => {
        el.onclick = () => {
          console.log('selected');
        }
      }
    )
    console.log('page has been scrubbed')
  }
  }
