        //   // allowsSelection={true}
        //   // allowsMultipleSelection={false}
        //   // canReorderItems={true}
        //   // canShrinkColumns={true}
        //   // render={this.render}
        // >
        // </TableView>
      )
    }
  //   componentDidMount() {
  //     setTimeout(() => {
  //         window.dispatchEvent(new Event('resize'));
  //     },100);
  // }
  }
